import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { first } from 'rxjs/operators';
import { ToastService } from '../toast.service';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss']
})
export class SignupComponent implements OnInit {
  registerForm: FormGroup;
  loading = false;
  submitted = false;
  acctypes = [
    { value: 'bm', viewValue: 'Business Maneger' },
    { value: 'pm', viewValue: 'Project Manager' }
  ]
  access = 'pm'
  loadingImage = "data:image/gif;base64,"
  warning: String = '';
  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    public toast: ToastService) {


  }

  ngOnInit() {
    this.registerForm = this.formBuilder.group({
      firstName: ['', Validators.required],
      lastName: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      userRole:[]
    });

  }

  // convenience getter for easy access to form fields
  get f() { return this.registerForm.controls; }

  onSelect(e) {
    this.warning = "";
  }
  onSubmit() {
    this.submitted = true;
    if (this.access.length === 0) {
      this.warning = "Please select a type";
      return;
    }
    if (this.registerForm.invalid) {
      return;
    }
    if (this.access === 'pm') {
      this.toast.presentToast("project manager is wroking");
    } else {
      this.toast.showErorr("error toast is working");
    }
    const { email, password } = this.registerForm.value;
  }
}
